precision highp float;
#if USE_ALPHA_TEST
  uniform ALPHA_TEST {
    float alphaThreshold;
  };
#endif
void ALPHA_TEST (in vec4 color) {
  #if USE_ALPHA_TEST
      if (color.a < alphaThreshold) discard;
  #endif
}
void ALPHA_TEST (in float alpha) {
  #if USE_ALPHA_TEST
      if (alpha < alphaThreshold) discard;
  #endif
}
in vec4 v_color;
in vec2 v_uv0;
uniform sampler2D texture;
uniform Properties {
  vec2 nodeSize;
  vec2 tileSize;
};
void main () {
  vec4 o = vec4(1, 1, 1, 1);
  vec2 uv = v_uv0;
  if (tileSize.x != .0 && tileSize.y != .0) {
    vec2 ratio = nodeSize / tileSize;
    uv = ceil(v_uv0 * ratio) / ratio;
  }
  vec4 texture_tmp = texture(texture, uv);
  #if CC_USE_ALPHA_ATLAS_texture
      texture_tmp.a *= texture(texture, uv + vec2(0, 0.5)).r;
  #endif
  #if INPUT_IS_GAMMA
    o.rgb *= (texture_tmp.rgb * texture_tmp.rgb);
    o.a *= texture_tmp.a;
  #else
    o *= texture_tmp;
  #endif
  o *= v_color;
  ALPHA_TEST(o);
  gl_FragColor = o;
}

