precision highp float;
uniform CCGlobal {
  mat4 cc_matView;
  mat4 cc_matViewInv;
  mat4 cc_matProj;
  mat4 cc_matProjInv;
  mat4 cc_matViewProj;
  mat4 cc_matViewProjInv;
  vec4 cc_cameraPos;
  vec4 cc_time;
  mediump vec4 cc_screenSize;
  mediump vec4 cc_screenScale;
};
in vec3 a_position;
in vec2 a_uv0;
in vec4 a_color;
out vec2 v_uv0;
out vec4 v_color;
void main () {
  gl_Position = cc_matViewProj * vec4(a_position, 1);
  v_uv0 = a_uv0;
  v_color = a_color;
}

glsl3.frag:
precision highp float;
in vec2 v_uv0;
in vec4 v_color;
uniform sampler2D texture;
uniform Properties {
  float round;
  float feather;
};
void main () {
  vec4 color = v_color;
  color *= texture(texture, v_uv0);
  if (color.a == 0.0) discard;
  if(round > 0.0){
    vec2 vertex;
    if (v_uv0.x <= round) {
      if (v_uv0.y <= round) {
        vertex = vec2(round, round);
      } else if (v_uv0.y >= 1.0 - round) {
        vertex = vec2(round, (1.0 - round));
      } else {
        vertex = vec2(round, v_uv0.y);
      }
    } else if (v_uv0.x >= 1.0 - round) {
      if (v_uv0.y <= round){
        vertex = vec2(1.0 - round, round);
      } else if (v_uv0.y >= 1.0 - round) {
        vertex = vec2(1.0 - round, (1.0 - round));
      } else {
        vertex = vec2(1.0 - round, v_uv0.y);
      }
    } else if (v_uv0.y <= round) {
      vertex = vec2(v_uv0.x, round);
    } else if (v_uv0.y >= 1.0 - round) {
      vertex = vec2(v_uv0.x, (1.0 - round));
    } else {
      vertex = v_uv0;
    }
    float dis = distance(v_uv0, vertex);
    color.a = smoothstep(round, round - feather, dis);
  }
  color.a *= v_color.a;
  gl_FragColor = color;
}

