glsl3.frag:
precision highp float;
in vec2 v_uv0;
in vec4 v_color;
uniform sampler2D texture;
uniform Properties {
  vec2 size;
  float radius;
};
const float MAX_DIAMETER = 100.0;
vec4 getBlurColor (vec2 uv, float radius) {
  vec4 color = vec4(0);
  float sum = 0.0;
  float diameter = radius * 2.0;
  for (float r = 0.0; r <= MAX_DIAMETER; r++) {
    if (r > diameter) break;
    float _r = r - radius;
    float x = uv.x + _r / size.x;
    if (x < 0.0 || x > 1.0 ) continue;
    for (float c = 0.0; c <= MAX_DIAMETER; c++) {
      if (c > diameter) break;
      float _c = c - radius;
      float y = uv.y + _c / size.y;
      if (y < 0.0 || y > 1.0 ) continue;
      vec2 target = vec2(x, y);
      float weight = (radius - abs(_r)) * (radius - abs(_c));
      color += texture2D(texture, target) * weight;
      sum += weight;
    }
  }
  color /= sum;
  return color;
}
void main () {
  vec4 color = v_color;
  color *= texture(texture, v_uv0);
  if (radius > 0.0){
    float _radius = radius > MAX_DIAMETER / 2.0 ? MAX_DIAMETER / 2.0 : radius;
    color = getBlurColor(v_uv0, _radius);
  }
  color.a *= v_color.a;
  gl_FragColor = color;
}

